import base64  # 内置模块

img_str = ''

"""将字符串形式的图片转换成二进制"""
bytes_img = base64.b64decode(img_str)
print(bytes_img)

with open('yzm.png', mode='wb') as f:
    f.write(bytes_img)


"""将二进制的 图片数据转换成二进制的 字符串形式"""
with open('yzm.png', mode='rb') as f:
    img_bytes = f.read()

str_img = base64.b64encode(img_bytes).decode()  # 二进制的 图片字节流数据 base64编码成 --> 二进制的 字符串字节流数据 -->  base64解码成字符
print('转化后的字符串图片:', str_img)